mapper.insert:
	INSERT INTO test_model (
         org_nm
         , model_nm
         
         , type_nm
         , result_nm
         , inst_flag
         , err_flag
      ) VALUES (
         #{org_nm}
         , #{model_nm}
         
         , #{type_nm}
         , #{result_nm}
         , #{inst_flag}
         , 1
      )

mapper.select:
SELECT item_sn , org_nm , model_nm , DATE_FORMAT(model_dttm,'%Y-%m-%d %H:%i:%s') AS model_dttm , type_nm , result_nm , inst_flag , err_flag FROM test_model WHERE 1=1
AND err_flag = #{err_flag}
AND result_nm = #{result_nm}
AND model_dttm > STR_TO_DATE(#{stdt}, '%Y-%m-%d')
AND model_dttm < DATE_ADD(STR_TO_DATE(#{eddt}, '%Y-%m-%d'), INTERVAL 1 DAY)
ORDER BY item_sn DESC
